import React from 'react';
import './Canvas.css';

function Canvas({ elements }) {
  const handleDrop = (e) => {
    e.preventDefault();
    const label = e.dataTransfer.getData('text/plain');
    // Here you can add more logic to position the dropped element on the canvas
    console.log('Dropped:', label);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  return (
    <div className='canvas' onDrop={handleDrop} onDragOver={handleDragOver}>
      {/* Render elements on canvas */}
      {elements.map((element, index) => (
        <div key={index} className='canvas-element'>
          {element}
        </div>
      ))}
    </div>
  );
}

export default Canvas;
