import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import './Modal.css'; // Assuming you have a CSS file for modal styles

const Modal = ({ element, onSaveChanges, onClose }) => {
  const [x, setX] = useState(element.x || '');
  const [y, setY] = useState(element.y || '');
  const [fontSize, setFontSize] = useState(element.fontSize || '');
  const [fontWeight, setFontWeight] = useState(element.fontWeight || '');
  const [labelText, setLabelText] = useState(element.text || 'Label'); // Default to 'Label' if not provided

  const handleSave = () => {
    onSaveChanges({
      x: x || 0,
      y: y || 0,
      fontSize: fontSize || '16px', // Default value if not provided
      fontWeight: fontWeight || 'normal', // Default value if not provided
      text: labelText
    });
  };

  const handleClose = () => {
    onClose();
  };

  return (
    <div className="modal-container">
      <div className="modal">
        <div className="edit-label">Edit {element.text} here</div>
        <FontAwesomeIcon icon={faTimes} className="close-icon" onClick={handleClose} />
        <div className="line"></div>
        <div className="input-container">
          <label htmlFor="labelTextInput">Text</label>
          <input
            type="text"
            id="labelTextInput"
            placeholder="This is label"
            value={labelText}
            onChange={(e) => setLabelText(e.target.value)}
            className="large-text-input" // Apply CSS class for large text input
          />
        </div>
        <div className="input-container">
          <label htmlFor="xInput">X</label>
          <input
            type="text"
            id="xInput"
            placeholder="Enter X coordinate"
            value={x}
            onChange={(e) => setX(e.target.value)}
          />
        </div>
        <div className="input-container">
          <label htmlFor="yInput">Y</label>
          <input
            type="text"
            id="yInput"
            placeholder="Enter Y coordinate"
            value={y}
            onChange={(e) => setY(e.target.value)}
          />
        </div>
        <div className="input-container">
          <label htmlFor="fontSizeInput">Font Size</label>
          <input
            type="text"
            id="fontSizeInput"
            placeholder="Enter font size"
            value={fontSize}
            onChange={(e) => setFontSize(e.target.value)}
          />
        </div>
        <div className="input-container">
          <label htmlFor="fontWeightInput">Font Weight</label>
          <input
            type="text"
            id="fontWeightInput"
            placeholder="Enter font weight"
            value={fontWeight}
            onChange={(e) => setFontWeight(e.target.value)}
          />
        </div>
        <button className="save-button" onClick={handleSave}>
          Save Changes
        </button>
      </div>
    </div>
  );
};

export default Modal;
