import React from 'react';
import ElementItem from '../ElementItem/ElementItem';
import './elementList.css';

const initialConfig = {
  text: '',
  x: '',
  y: '',
  fontSize: '',
  fontWeight: '',
};

const elements = [
  {
    id: '',
    elementType: 'Label',
    config: { ...initialConfig },
  },
  {
    id: '',
    elementType: 'Input',
    config: { ...initialConfig },
  },
  {
    id: '',
    elementType: 'Button',
    config: { ...initialConfig },
  },
];

const ElementList = () => {
  const [draggingElement, setDraggingElement] = useState(null);

  const handleDragStart = (e, element) => {
    setDraggingElement(element);
    e.dataTransfer.setData('elementType', element.elementType);
    const dragImage = document.createElement('div');
    dragImage.style.width = '100px';
    dragImage.style.height = '40px';
    dragImage.style.background = 'rgba(0, 0, 0, 0.1)';
    document.body.appendChild(dragImage);
    e.dataTransfer.setDragImage(dragImage, 50, 20);
    setTimeout(() => document.body.removeChild(dragImage), 0);
  };

  const handleDragEnd = () => {
    setDraggingElement(null);
  };

  const isElementDragging = (element) => {
    return draggingElement?.elementType === element.elementType;
  };

  return (
    <div className="ElementList">
      {elements.map((element) => (
        <div
          key={element.elementType}
          draggable
          onDragStart={(e) => handleDragStart(e, element)}
          onDragEnd={handleDragEnd}
          className="ElementList__item"
        >
          <ElementItem dragging={isElementDragging(element)} name={element.elementType} />
        </div>
      ))}
    </div>
  );
};

export default ElementList;
