import React, { useState } from 'react';
import './App.css';
import Canvas from './Canvas';
import Sidebar from './Sidebar';

function App() {
  const [canvasElements, setCanvasElements] = useState([]);

  const handleDrop = (label) => {
    // Add the dropped label to the canvas elements state
    setCanvasElements([...canvasElements, label]);
  };

  return (
    <div className="App">
      <Canvas elements={canvasElements} />
      <Sidebar onDrop={handleDrop} />
    </div>
  );
}

export default App;
