// ElementContext.js
import React, { createContext, useState } from 'react';

const ElementContext = createContext();

const ElementProvider = ({ children }) => {
  const [elements, setElements] = useState([]);

  const addElement = (newElement) => {
    setElements([...elements, newElement]);
  };

  const updateElement = (id, updatedProperties) => {
    setElements(elements.map(el => el.id === id ? { ...el, ...updatedProperties } : el));
  };

  const deleteElement = (id) => {
    setElements(elements.filter(el => el.id !== id));
  };

  return (
    <ElementContext.Provider value={{ elements, addElement, updateElement, deleteElement }}>
      {children}
    </ElementContext.Provider>
  );
};

export { ElementContext, ElementProvider };
