import React, { useState, useEffect } from 'react';
import './Modal.css';

function Modal({ element, onSaveChanges, onClose }) {
  const [text, setText] = useState('');
  const [xCoordinate, setXCoordinate] = useState('');
  const [yCoordinate, setYCoordinate] = useState('');
  const [fontSize, setFontSize] = useState('');
  const [fontWeight, setFontWeight] = useState('');

  useEffect(() => {
    if (element) {
      setText(element.text || '');
      setXCoordinate(element.x || '');
      setYCoordinate(element.y || '');
      setFontSize(element.fontSize || '');
      setFontWeight(element.fontWeight || '');
    } else {
      setText('');
      setXCoordinate('');
      setYCoordinate('');
      setFontSize('');
      setFontWeight('');
    }
  }, [element]);

  // Determine the title based on element type
  let modalTitle = '';
  switch (element?.type) {
    case 'label':
      modalTitle = 'Edit Label';
      break;
    case 'input':
      modalTitle = 'Edit Input';
      break;
    case 'button':
      modalTitle = 'Edit Button';
      break;
    default:
      modalTitle = 'Edit Element';
      break;
  }

  const handleSave = () => {
    const config = {
      text: text || (element.type === 'input' ? 'Input' : 'Button'),
      x: parseInt(xCoordinate) || 0, // Ensure coordinates are integers
      y: parseInt(yCoordinate) || 0,
      fontSize: fontSize || '16px',
      fontWeight: fontWeight || 'normal'
    };

    onSaveChanges(config);
  };

  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <h2>{modalTitle}</h2>
        <label>
          Text:
          <input
            type="text"
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
        </label>
        <label>
          X Coordinate:
          <input
            type="number"
            value={xCoordinate}
            onChange={(e) => setXCoordinate(e.target.value)}
          />
        </label>
        <label>
          Y Coordinate:
          <input
            type="number"
            value={yCoordinate}
            onChange={(e) => setYCoordinate(e.target.value)}
          />
        </label>
        <label>
          Font Size:
          <input
            type="text"
            value={fontSize}
            onChange={(e) => setFontSize(e.target.value)}
          />
        </label>
        <label>
          Font Weight:
          <select
            value={fontWeight}
            onChange={(e) => setFontWeight(e.target.value)}
          >
            <option value="normal">Normal</option>
            <option value="bold">Bold</option>
            <option value="bolder">Bolder</option>
            <option value="lighter">Lighter</option>
          </select>
        </label>
        <div className="modal-buttons">
          <button onClick={handleSave}>Save</button>
          <button onClick={onClose}>Cancel</button>
        </div>
      </div>
    </div>
  );
}

export default Modal;
