import React from 'react';
import './elementWrapper.css';

const ElementWrapper = ({ element, onDrag, onClick }) => {
  const handleDragStart = (e) => {
    e.dataTransfer.setData('id', element.id);
  };

  return (
    <div
      className={`element-wrapper`}
      style={{
        position: 'absolute',
        top: element.y,
        left: element.x,
        fontSize: element.fontSize,
        fontWeight: element.fontWeight,
        border: element.active ? '2px solid red' : '2px solid transparent'
      }}
      draggable
      onDragStart={handleDragStart}
      onDrag={onDrag}
      onClick={onClick}
    >
      {element.type === 'Label' && element.text}
      {element.type === 'Input' && <input type="text" placeholder={element.text} />}
      {element.type === 'Button' && <button>{element.text}</button>}
    </div>
  );
};

export default ElementWrapper;
