import React, { useState, useEffect } from 'react';
import FormButton from '../FormButton/FormButton';
import FormInput from '../FormInput/FormInput';
import styles from './modal.module.css';

const Modal = ({ open, selectedElement, initialElement, closeModal, onElementSave, onNewElementSave }) => {
  const [elementConfig, setElementConfig] = useState({
    text: '',
    x: '',
    y: '',
    fontSize: '',
    fontWeight: '',
  });

  useEffect(() => {
    if (selectedElement) {
      setElementConfig(selectedElement.config);
    } else if (initialElement) {
      setElementConfig(initialElement.config);
    }
  }, [selectedElement, initialElement]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setElementConfig({
      ...elementConfig,
      [name]: value,
    });
  };

  const handleSave = (e) => {
    e.preventDefault();
    if (selectedElement) {
      onElementSave(selectedElement.id, elementConfig);
    } else {
      onNewElementSave(elementConfig);
    }
  };

  return (
    open && (
      <div className={styles.ModalOverlay}>
        <div className={styles.Modal}>
          <h2>{selectedElement ? 'Edit Element' : 'Add Element'}</h2>
          <form onSubmit={handleSave}>
            <FormInput
              label="Text"
              name="text"
              type="text"
              value={elementConfig.text}
              onChange={handleChange}
            />
            <FormInput
              label="X Position"
              name="x"
              type="number"
              value={elementConfig.x}
              onChange={handleChange}
            />
            <FormInput
              label="Y Position"
              name="y"
              type="number"
              value={elementConfig.y}
              onChange={handleChange}
            />
            <FormInput
              label="Font Size"
              name="fontSize"
              type="number"
              value={elementConfig.fontSize}
              onChange={handleChange}
            />
            <FormInput
              label="Font Weight"
              name="fontWeight"
              type="number"
              value={elementConfig.fontWeight}
              onChange={handleChange}
            />
            <div className={styles.ModalButtons}>
              <FormButton type="submit">Save</FormButton>
              <FormButton type="button" onClick={closeModal}>
                Cancel
              </FormButton>
            </div>
          </form>
        </div>
      </div>
    )
  );
};

export default Modal;
