import React, { useState } from 'react';
import './modal.css';

const Modal = ({ config, onSave }) => {
  const [localConfig, setLocalConfig] = useState(config);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLocalConfig({ ...localConfig, config: { ...localConfig.config, [name]: value } });
  };

  return (
    <div className="modal-backdrop">
      <div className="modal">
        <h3>Edit {config.type}</h3>
        <input name="text" value={localConfig.config.text} onChange={handleChange} placeholder="Text" />
        <input name="fontSize" value={localConfig.config.fontSize} onChange={handleChange} placeholder="Font Size" type="number" />
        <input name="fontWeight" value={localConfig.config.fontWeight} onChange={handleChange} placeholder="Font Weight" />
        <button onClick={() => onSave(localConfig)}>Save Changes</button>
      </div>
    </div>
  );
};

export default Modal;
