import React from 'react';
import './Canvas.css';

const Canvas = ({ elements, onDragOver, onDrop }) => {
  const handleDrop = (e) => {
    e.preventDefault();
    const item = JSON.parse(e.dataTransfer.getData('item'));
    const x = e.clientX - e.target.offsetLeft;
    const y = e.clientY - e.target.offsetTop;
    const newElement = { ...item, x, y };
    // Handle adding newElement to your state or context
    console.log('Dropped:', newElement);
  };

  return (
    <div className='canvas' onDragOver={onDragOver} onDrop={handleDrop}>
      {elements.map((element, index) => (
        <div
          key={index}
          className={`dropped-element ${element.type}`} // Ensure class name matches sidebar element type
          style={{
            position: 'absolute',
            left: element.x,
            top: element.y,
            width: '278px', // Adjust width to match sidebar element width
            height: '49px', // Adjust height to match sidebar element height
            background: '#FFFFFF',
            boxShadow: '0px 4px 24px rgba(0, 0, 0, 0.07)',
            borderRadius: '4px',
          }}
        >
          {/* Content of the dropped element */}
          {element.type}
        </div>
      ))}
    </div>
  );
};

export default Canvas;
