import React, { useState } from 'react';
import './board.css';
import Modal from '../Modal/Modal';
import ElementWrapper from '../ElementWrapper/ElementWrapper';

const Board = () => {
  const [elements, setElements] = useState([]);
  const [currentElement, setCurrentElement] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [isNewElement, setIsNewElement] = useState(false);

  const handleDrop = (e) => {
    e.preventDefault();
    const type = e.dataTransfer.getData('type');
    if (!type) return; // If there's no type, it's an existing element

    const newElement = {
      id: Date.now(),
      type,
      x: e.clientX,
      y: e.clientY,
      config: { text: '', fontSize: 14, fontWeight: 'normal' } // Default config
    };
    setCurrentElement(newElement);
    setModalConfig(newElement);
    setModalVisible(true);
    setIsNewElement(true);
  };

  const saveElement = (config) => {
    if (isNewElement) {
      setElements([...elements, config]);
    } else {
      setElements(elements.map(el => el.id === config.id ? config : el));
    }
    setModalVisible(false);
    setIsNewElement(false);
  };

  const handleElementDrag = (e, id) => {
    const updatedElements = elements.map(el => el.id === id ? {
      ...el,
      x: e.clientX,
      y: e.clientY
    } : el);
    setElements(updatedElements);
  };

  return (
    <div className="board" onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
      {elements.map((el) => (
        <ElementWrapper
          key={el.id}
          element={el}
          onDrag={(e) => handleElementDrag(e, el.id)}
        />
      ))}
      {modalVisible && <Modal config={modalConfig} onSave={saveElement} />}
    </div>
  );
};

export default Board;
