import React from 'react';
import './elementWrapper.css';

const ElementWrapper = ({ element, onDrag }) => {
  const handleDragStart = (e) => {
    e.dataTransfer.setData('type', '');
  };

  return (
    <div
      className="element-wrapper"
      style={{ left: element.x, top: element.y }}
      draggable
      onDragStart={handleDragStart}
      onDragEnd={(e) => onDrag(e)}
    >
      {element.type === 'label' && <div className="label">{element.config.text}</div>}
      {element.type === 'input' && <input className="input" placeholder={element.config.text} />}
      {element.type === 'button' && <button className="button">{element.config.text}</button>}
    </div>
  );
};

export default ElementWrapper;
