// Canvas.js

import React, { useState, useEffect } from 'react';
import Modal from './Modal';
import './Canvas.css';

function Canvas() {
  const [elements, setElements] = useState([]);
  const [selectedElement, setSelectedElement] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const savedElements = JSON.parse(localStorage.getItem('canvasElements')) || [];
    setElements(savedElements);
  }, []);

  useEffect(() => {
    localStorage.setItem('canvasElements', JSON.stringify(elements));
  }, [elements]);

  const handleDragStart = (event, elementType) => {
    event.dataTransfer.setData('elementType', elementType);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const elementType = event.dataTransfer.getData('elementType');

    // Calculate position based on mouse coordinates
    const x = event.clientX - event.target.offsetLeft;
    const y = event.clientY - event.target.offsetTop;

    const newElement = {
      type: elementType,
      x,
      y,
      // Additional configuration properties can be added here
    };

    setElements([...elements, newElement]);
  };

  const handleElementClick = (element) => {
    setSelectedElement(element);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const handleSaveModal = (event) => {
    event.preventDefault();
    // Handle saving changes to element configuration
    setIsModalOpen(false);
  };

  const handleDeleteElement = () => {
    const updatedElements = elements.filter((element) => element !== selectedElement);
    setElements(updatedElements);
    setSelectedElement(null);
    setIsModalOpen(false); // Close modal after deletion
  };

  return (
    <div className='canvas' onDragOver={handleDragOver} onDrop={handleDrop}>
      {elements.map((element, index) => (
        <div
          key={index}
          className={`element ${element.type} ${selectedElement === element ? 'selected' : ''}`}
          style={{ top: element.y, left: element.x }}
          onClick={() => handleElementClick(element)}
        >
          {element.type}
        </div>
      ))}
      <Modal
        isOpen={isModalOpen}
        handleClose={handleCloseModal}
        handleSave={handleSaveModal}
        handleDelete={handleDeleteElement}
        element={selectedElement}
      />
    </div>
  );
}

export default Canvas;
