import React, { useState } from 'react';

function Modal({ element, onSaveChanges, onClose }) {
  const [text, setText] = useState(element.text);
  const [fontSize, setFontSize] = useState(element.fontSize);
  const [fontWeight, setFontWeight] = useState(element.fontWeight);

  const handleSave = () => {
    onSaveChanges({
      text: text || 'Label',
      fontSize: fontSize || '16px',
      fontWeight: fontWeight || 'normal'
      // Add other configurations
    });
  };

  return (
    <div className='modal'>
      <div className='modal-content'>
        <h2>Edit {element.type} here</h2>
        <form onSubmit={handleSave}>
          <label>Label Text:</label>
          <input type='text' value={text} onChange={(e) => setText(e.target.value)} />

          <label>X Coordinate:</label>
          <input type='text' value={element.x} readOnly />

          <label>Y Coordinate:</label>
          <input type='text' value={element.y} readOnly />

          <label>Font Size:</label>
          <input type='text' value={fontSize} onChange={(e) => setFontSize(e.target.value)} />

          <label>Font Weight:</label>
          <input type='text' value={fontWeight} onChange={(e) => setFontWeight(e.target.value)} />

          <div className='modal-buttons'>
            <button type='submit'>Save Changes</button>
            <button onClick={onClose}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Modal;
