import React, { useState } from 'react';
import Modal from './Modal';
import './Canvas.css'; // Import CSS file for canvas styles

function Canvas() {
  const [elements, setElements] = useState([]);
  const [selectedElement, setSelectedElement] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  const handleDrop = (event) => {
    event.preventDefault();
    const elementType = event.dataTransfer.getData('text/plain');
    const { clientX, clientY } = event;

    const newElement = {
      id: elements.length + 1, // Unique ID for new elements
      type: elementType,
      x: clientX,
      y: clientY,
      text: 'Label', // Default text
      fontSize: '16px', // Default font size
      fontWeight: 'normal' // Default font weight
      // Add other default configurations
    };

    setElements([...elements, newElement]);
  };

  const handleElementClick = (element) => {
    setSelectedElement(element);
    setModalOpen(true);
  };

  const handleSaveChanges = (config) => {
    const updatedElements = elements.map((el) =>
      el.id === selectedElement.id ? { ...el, ...config } : el
    );

    setElements(updatedElements);
    setModalOpen(false);
    setSelectedElement(null);
  };

  const handleDeleteElement = () => {
    const updatedElements = elements.filter((el) => el.id !== selectedElement.id);
    setElements(updatedElements);
    setModalOpen(false);
    setSelectedElement(null);
  };

  return (
    <div className='canvas' onDragOver={(e) => e.preventDefault()} onDrop={handleDrop}>
      {/* Render dropped elements */}
      {elements.map((el) => (
        <div
          key={el.id}
          className='element'
          style={{ left: el.x, top: el.y }}
          onClick={() => handleElementClick(el)}
          draggable={true}
          onDragStart={(event) => {
            event.dataTransfer.setData('text/plain', el.type);
            event.currentTarget.style.cursor = 'grabbing'; // Change cursor to grabbing on drag start
          }}
          onDragEnd={(event) => {
            event.currentTarget.style.cursor = 'grab'; // Restore cursor to grab on drag end
          }}
        >
          {el.text}
        </div>
      ))}

      {/* Modal for configuration */}
      {modalOpen && (
        <Modal
          element={selectedElement}
          onSaveChanges={handleSaveChanges}
          onDelete={handleDeleteElement}
          onClose={() => setModalOpen(false)}
        />
      )}
    </div>
  );
}

export default Canvas;
