import React from 'react';
import styles from './elementWrapper.css';

const ElementWrapper = ({ element, updateBoardElement, setSelectedElement, selectedElement, children }) => {
  const handleMouseDown = (e) => {
    setSelectedElement(element);
    const initialX = e.clientX;
    const initialY = e.clientY;
    const initialElementX = parseInt(element.config.x, 10);
    const initialElementY = parseInt(element.config.y, 10);

    const handleMouseMove = (moveEvent) => {
      const deltaX = moveEvent.clientX - initialX;
      const deltaY = moveEvent.clientY - initialY;

      updateBoardElement(element.id, {
        ...element.config,
        x: initialElementX + deltaX,
        y: initialElementY + deltaY,
      });
    };

    const handleMouseUp = () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };

    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);
  };

  const style = {
    position: 'absolute',
    left: `${element.config.x}px`,
    top: `${element.config.y}px`,
    cursor: selectedElement && selectedElement.id === element.id ? 'grabbing' : 'grab',
  };

  return (
    <div
      className={styles.ElementWrapper}
      style={style}
      onMouseDown={handleMouseDown}
    >
      {children}
    </div>
  );
};

export default ElementWrapper;
