import React, { useState, useEffect } from 'react';

function Modal({ element, onSaveChanges, onClose }) {
  const [label, setLabel] = useState('');
  const [xCoordinate, setXCoordinate] = useState('');
  const [yCoordinate, setYCoordinate] = useState('');

  useEffect(() => {
    if (element) {
      setLabel(element.label || '');
      setXCoordinate(element.x || '');
      setYCoordinate(element.y || '');
    } else {
      // Handle default values or leave empty
      setLabel('');
      setXCoordinate('');
      setYCoordinate('');
    }
  }, [element]);

  const handleSave = () => {
    onSaveChanges({
      label,
      x: xCoordinate,
      y: yCoordinate
      // Add other properties as needed
    });
  };

  return (
    <div className="modal-container">
      <div className="modal">
        <div className="edit-label">Edit Label</div>
        <span className="close-icon" onClick={onClose}>
          &times;
        </span>
        <div className="line"></div>
        
        <div className="input-container">
          <label htmlFor="labelInput">Label:</label>
          <input
            type="text"
            id="labelInput"
            value={label}
            onChange={(e) => setLabel(e.target.value)}
            placeholder="Enter label"
            className="large-input"
          />
        </div>
        
        <div className="input-container">
          <label htmlFor="xInput">X Coordinate:</label>
          <input
            type="text"
            id="xInput"
            value={xCoordinate}
            onChange={(e) => setXCoordinate(e.target.value)}
            placeholder="Enter X coordinate"
            className="large-input"
          />
        </div>
        
        <div className="input-container">
          <label htmlFor="yInput">Y Coordinate:</label>
          <input
            type="text"
            id="yInput"
            value={yCoordinate}
            onChange={(e) => setYCoordinate(e.target.value)}
            placeholder="Enter Y coordinate"
            className="large-input"
          />
        </div>
        
        <button className="save-button" onClick={handleSave}>
          Save Changes
        </button>
      </div>
    </div>
  );
}

export default Modal;
