import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGripVertical } from '@fortawesome/free-solid-svg-icons';
import './Sidebar.css';

const Sidebar = ({ onDragStart }) => {
  const handleDragStart = (e, item) => {
    // Create a new drag image element
    const dragImage = new Image();
    dragImage.src = ''; // Set the source of your custom drag image if needed

    // Set a transparent image as drag image (to avoid default browser behavior)
    e.dataTransfer.setDragImage(dragImage, 0, 0);

    onDragStart(e, item);
  };

  return (
    <div className='sidebar'>
      <div className='content'>
        <div className='block'>BLOCKS</div>
        <div
          className='blocks'
          draggable
          onDragStart={(e) => handleDragStart(e, { type: 'label' })}
        >
          <div className='icon'>
            <FontAwesomeIcon icon={faGripVertical} className='grip' style={{ color: "#D4D4D4" }} />
          </div>
          <div className='label'>Label</div>
        </div>
        <div
          className='blocks'
          draggable
          onDragStart={(e) => handleDragStart(e, { type: 'input' })}
        >
          <div className='icon'>
            <FontAwesomeIcon icon={faGripVertical} className='grip' style={{ color: "#D4D4D4" }} />
          </div>
          <div className='label'>Input</div>
        </div>
        <div
          className='blocks'
          draggable
          onDragStart={(e) => handleDragStart(e, { type: 'button' })}
        >
          <div className='icon'>
            <FontAwesomeIcon icon={faGripVertical} className='grip' style={{ color: "#D4D4D4" }} />
          </div>
          <div className='label'>Button</div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
