import React, { useState } from 'react';
import Modal from './Modal';
import './Canvas.css'; // CSS file for specific canvas styles

function Canvas() {
  const [elements, setElements] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [draggedElement, setDraggedElement] = useState(null); // Track the element being dragged

  const handleDrop = (event) => {
    event.preventDefault();
    const elementType = event.dataTransfer.getData('text/plain');
    const { clientX, clientY } = event;

    const newElement = {
      type: elementType,
      x: clientX,
      y: clientY,
      text: 'Label', // Default text
      fontSize: '16px', // Default font size
      fontWeight: 'normal' // Default font weight
      // Add other default configurations
    };

    setModalOpen(true); // Open modal for configuration
    setDraggedElement(newElement); // Track the element being dragged
  };

  const handleSaveChanges = (config) => {
    const updatedElement = {
      ...draggedElement,
      ...config
    };

    setElements([...elements, updatedElement]); // Add to canvas elements
    setModalOpen(false); // Close modal
    setDraggedElement(null); // Clear dragged element
  };

  return (
    <div className='canvas' onDragOver={(e) => e.preventDefault()} onDrop={handleDrop}>
      {/* Render dropped elements */}
      {elements.map((el, index) => (
        <div
          key={index}
          className='element'
          style={{ left: el.x, top: el.y }}
          draggable={true}
          onDragStart={(event) => {
            event.dataTransfer.setData('text/plain', 'Label');
            event.currentTarget.style.opacity = '0.4'; // Reduce opacity of dragged element
            setDraggedElement(el); // Track the element being dragged
          }}
          onDragEnd={(event) => {
            event.currentTarget.style.opacity = '1'; // Restore opacity on drag end
          }}
        >
          {el.text}
        </div>
      ))}

      {/* Render dragged element */}
      {draggedElement && (
        <div
          className='element'
          style={{ left: draggedElement.x, top: draggedElement.y, opacity: '0.4' }}
        >
          {draggedElement.text}
        </div>
      )}

      {/* Modal for configuration */}
      {modalOpen && (
        <Modal
          element={draggedElement}
          onSaveChanges={handleSaveChanges}
          onClose={() => setModalOpen(false)}
        />
      )}
    </div>
  );
}

export default Canvas;
