import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import './Modal.css';

const Modal = ({ elementName, onSave, onClose }) => {
  const [x, setX] = useState('');
  const [y, setY] = useState('');
  const [fontSize, setFontSize] = useState('');
  const [fontWeight, setFontWeight] = useState('');

  const handleSave = () => {
    onSave({
      x: x || 0,
      y: y || 0,
      fontSize: fontSize || '14px',
      fontWeight: fontWeight || 'normal',
    });
  };

  const handleClose = () => {
    onClose();
  };

  return (
    <div className="modal-container">
      <div className="modal">
        <div className="edit-label">Edit {elementName} here</div>
        <FontAwesomeIcon icon={faTimes} className="close-icon" onClick={handleClose} />
        <div className="line"></div>
        <div className="input-container">
          <label htmlFor="labelInput">Label</label>
          <input
            type="text"
            id="labelInput"
            placeholder="This is label"
            value={elementName}
            readOnly
          />
        </div>
        <div className="input-container">
          <label htmlFor="xInput">X</label>
          <input
            type="text"
            id="xInput"
            placeholder="X coordinate"
            value={x}
            onChange={(e) => setX(e.target.value)}
          />
        </div>
        <div className="input-container">
          <label htmlFor="yInput">Y</label>
          <input
            type="text"
            id="yInput"
            placeholder="Y coordinate"
            value={y}
            onChange={(e) => setY(e.target.value)}
          />
        </div>
        <div className="input-container">
          <label htmlFor="fontSizeInput">Font Size</label>
          <input
            type="text"
            id="fontSizeInput"
            placeholder="Font size"
            value={fontSize}
            onChange={(e) => setFontSize(e.target.value)}
          />
        </div>
        <div className="input-container">
          <label htmlFor="fontWeightInput">Font Weight</label>
          <input
            type="text"
            id="fontWeightInput"
            placeholder="Font weight"
            value={fontWeight}
            onChange={(e) => setFontWeight(e.target.value)}
          />
        </div>
        <button className="save-button" onClick={handleSave}>
          Save Changes
        </button>
      </div>
    </div>
  );
};

export default Modal;
