import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import Canvas from './Canvas';
import Sidebar from './Sidebar';

function App() {
  const [elements, setElements] = useState([]);

  const handleDrop = (item, x, y) => {
    const newElement = { ...item, x, y };
    setElements([...elements, newElement]);
  };

  const handleDragStart = (e, item) => {
    // Calculate offset to adjust mouse position relative to the dragged item
    const offsetX = e.nativeEvent.offsetX || e.nativeEvent.layerX;
    const offsetY = e.nativeEvent.offsetY || e.nativeEvent.layerY;
    e.dataTransfer.setData('item', JSON.stringify({ ...item, offsetX, offsetY }));
  };

  const handleCanvasDragOver = (e) => {
    e.preventDefault();
  };

  const handleCanvasDrop = (e) => {
    e.preventDefault();
    const item = JSON.parse(e.dataTransfer.getData('item'));
    const x = e.clientX - e.target.offsetLeft - item.offsetX;
    const y = e.clientY - e.target.offsetTop - item.offsetY;
    handleDrop(item, x, y);
  };

  return (
    <div className="App">
      <Canvas
        elements={elements}
        onDragOver={handleCanvasDragOver}
        onDrop={handleCanvasDrop}
      />
      <Sidebar onDragStart={handleDragStart} />
    </div>
  );
}

export default App
