import React, { useState, useEffect } from 'react';
import './board.css';
import Modal from '../Modal/Modal';
import ElementWrapper from '../ElementWrapper/ElementWrapper';

const Board = () => {
  const [elements, setElements] = useState(() => JSON.parse(localStorage.getItem('elements')) || []);
  const [currentElement, setCurrentElement] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [isNewElement, setIsNewElement] = useState(false);
  const [modalConfig, setModalConfig] = useState(null);

  useEffect(() => {
    localStorage.setItem('elements', JSON.stringify(elements));
  }, [elements]);

  const handleDrop = (e) => {
    e.preventDefault();
    const type = e.dataTransfer.getData('type');
    if (!type) return;

    const newElement = {
      id: Date.now(),
      type,
      x: e.clientX,
      y: e.clientY,
      text: getDefaultTextForElement(type), // Set default text based on element type
      fontSize: 14,
      fontWeight: 'normal'
    };
    setCurrentElement(newElement);
    setModalConfig(newElement);
    setModalVisible(true);
    setIsNewElement(true);
  };

  const getDefaultTextForElement = (type) => {
    switch (type) {
      case 'Label':
        return 'Label';
      case 'Input':
        return 'Placeholder';
      case 'Button':
        return 'Button';
      default:
        return '';
    }
  };

  const saveElement = (config) => {
    if (isNewElement) {
      setElements([...elements, config]);
    } else {
      setElements(elements.map(el => el.id === config.id ? config : el));
    }
    setModalVisible(false);
    setIsNewElement(false);
  };

  const handleElementDrag = (e, id) => {
    const updatedElements = elements.map(el => el.id === id ? {
      ...el,
      x: e.clientX,
      y: e.clientY
    } : el);
    setElements(updatedElements);
  };

  const handleElementClick = (element) => {
    setCurrentElement(element);
    setModalConfig(element);
    setModalVisible(true);
  };

  return (
    <div className="board" onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
      {elements.map((el) => (
        <ElementWrapper
          key={el.id}
          element={el}
          onDrag={(e) => handleElementDrag(e, el.id)}
          onClick={() => handleElementClick(el)}
        />
      ))}
      {modalVisible && <Modal config={modalConfig} onSave={saveElement} onClose={() => setModalVisible(false)} />}
    </div>
  );
};

export default Board;
