// Modal.js

import React from 'react';
//import './Modal.css';

function Modal({ isOpen, handleClose, handleSave, element }) {
  if (!isOpen) return null;

  return (
    <div className='modal'>
      <div className='modal-content'>
        <span className='close' onClick={handleClose}>&times;</span>
        <h2>Edit Element</h2>
        <form onSubmit={handleSave}>
          <label>X Coordinate:</label>
          <input type='text' value={element.x} readOnly />
          <label>Y Coordinate:</label>
          <input type='text' value={element.y} readOnly />
          {/* Additional input fields for element configuration */}
          <button type='submit'>Save Changes</button>
        </form>
      </div>
    </div>
  );
}

export default Modal;
