import React from 'react';
import './elementItem.css';

const ElementItem = ({ dragging, name, className = '', isShadowElement = false }) => {
  const getCustomClassNames = () => {
    if (isShadowElement) {
      return 'ElementItem__shadow';
    }
    if (dragging) {
      return 'ElementItem__dragging';
    }
    return '';
  };

  return (
    <div
      className={`ElementItem ${getCustomClassNames()} ${className}`}
      style={{ opacity: dragging && !isShadowElement ? 0.5 : 1 }}
    >
      <div className="ElementItem__icon">
        {/* Assuming you have an icon component or inline SVG here */}
        <i className="fa fa-grip-vertical"></i>
      </div>
      <div className="ElementItem__name">{name}</div>
    </div>
  );
};

export default ElementItem;
