import React, { useState } from 'react';
import './elementWrapper.css';

const ElementWrapper = ({ element }) => {
  const [position, setPosition] = useState({ x: element.x, y: element.y });
  const [isDragging, setIsDragging] = useState(false);

  const handleDragStart = (e) => {
    setIsDragging(true);
    e.dataTransfer.setData('text/plain', '');
  };

  const handleDragEnd = (e) => {
    setIsDragging(false);
    setPosition({ x: e.clientX, y: e.clientY });
  };

  return (
    <div
      className={`element-wrapper ${isDragging ? 'dragging' : ''}`}
      style={{ left: position.x, top: position.y }}
      draggable
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
    >
      {element.type === 'label' && <div>{element.config.text}</div>}
      {element.type === 'input' && <input placeholder={element.config.text} />}
      {element.type === 'button' && <button>{element.config.text}</button>}
    </div>
  );
};

export default ElementWrapper;
